import { Component, OnInit } from '@angular/core';
import { getLocaleDateFormat } from '@angular/common';

@Component({
  selector: 'app-exercice1',
  templateUrl: './exercice1.component.html',
  styleUrls: ['./exercice1.component.css']
})
export class Exercice1Component implements OnInit {

  // Exemple liaison d'attribut 
  avatar: string = 'data:image/jpeg;base64,';

  // Exemple liaison d'Ã©venement
  reverse: boolean = true;
  reverseImage(event: Event,): void {
    if(this.reverse){
      this.avatar = 'data:image/jpeg;base64,';
      this.reverse=!this.reverse;
    } else {
      this.avatar = 'data:image/jpeg;base64,';
      this.reverse=!this.reverse;
    }
  }
  
  // Exemple d'interpolation
  getDateNow(): any {
    const dateJour = new Date();
    return dateJour.getDate()+"/"+(dateJour.getMonth()+1)+"/"+dateJour.getFullYear();
  }
  
  
  constructor() { 
  
  }

  ngOnInit() {
    console.log()

  }

  
}
